import React from 'react'
import { Spinner } from 'react-bootstrap'

function Loader() {
    return (
        <Spinner
            animation='border'
            role='stauts'
            style={{
                height:'100px',
                width:'100px',
                margin:'auto',
                display:'block'
            }}
        >
            <span className='sr-only'>LOADING...</span>

        </Spinner>
    )
}

export default Loader
